    // Assuming today's date is December 30, 2014
    tm timeinfo = {0};
    timeinfo.tm_year = 2014 - 1900; // Year since 1900
    timeinfo.tm_mon = 11;           // Month (0-11, where January is 0)
    timeinfo.tm_mday = 30;          // Day of the month

    // Convert to time_t
    time_t today = mktime(&timeinfo);

    // Add one week (7 days)
