    time_t one_week_later = today + (7 * SECONDS_PER_DAY);

    // Convert back to tm struct
    tm *date_one_week_later = localtime(&one_week_later);

    // Print the date one week later in MM/DD/YYYY format using put_time
    cout << put_time(date_one_week_later, "%m/%d/%Y") << endl;

    return 0;
}


